mapper.insert:
insert into `order` (orderurl, ordertitle, ordertype, fensi, price, needcnt, hadcnt, userid
,orderstate
,hangye ) VALUES (#{orderurl}, #{ordertitle}, #{ordertype}, #{fensi}, #{price}, #{needcnt}, #{hadcnt}, #{userid}
,#{orderstate}
,#{hangye} )

mapper.select:
SELECT orderid, orderurl, ordertitle, ordertype, fensi, price, needcnt, hadcnt, userid, orderstate FROM `order` t WHERE ordertype = #{ordertype}
AND t.userid = #{userid}
and t.needcnt > t.hadcnt
and t.needcnt = t.hadcnt
order by occurdate desc

mapper.update:
UPDATE ordertake t
set t.takestate = #{takestate}

	, t.finishdate = #{finishdate}

where t.userid = #{userid}
  and t.orderid = #{orderid}

mapper.select:
SELECT userid, orderid, takestate
FROM ordertake t
where t.userid = #{userid}
  and t.orderid = #{orderid}
