mapper.select:
select count(1) from userinfo where password=#{password} 

	and (username=#{username} or phone=#{username})


	and phone=#{phone}

SELECT a.userid, a.username, a.phone, a.wechat, a.alipay, a.factname, a.integral
FROM userinfo a
where a.userid = a.userid

	and a.userid = #{userid}


	and a.username = #{username}


	and a.`password` = #{password}

SELECT t.zhuye, t.zantong, t.fensi, t.pttype, t.ptid, t.hangye, 
		ptname,yanzhi,ptid
	from userinfo_ext t
	where t.userid = #{userid}
	  and t.pttype = #{pttype}

mapper.update:
update userinfo_ext t
	set t.userid = t.userid
	
		,t.zhuye = #{zhuye}
	
	
		,t.zantong = #{zantong}
	
	
		,t.yanzhi = #{yanzhi}
	
	
		,t.ptname = #{ptname}
	
	
		,t.ptid = #{ptid}
	
	
		,t.hangye = #{hangye}
	
	
		,t.fensi = #{fensi}
	
	where t.userid = #{userid}
	  and t.pttype = #{pttype}

UPDATE userinfo t
	set t.username = t.username
	
		,t.wechat = #{wechat}
	
	
		,t.phone = #{phone}
	
	
	,t.alipay = #{alipay}
	
	
	,t.factname = #{factname}
	
	where t.userid = #{userid}
