mapper.select:
SELECT a.userid, a.username, a.phone, a.wechat, a.alipay, a.factname, b.integral
FROM userinfo a, Integral b
where a.userid = b.userid

	and a.userid = #{userid}


	and a.username = #{username}


	and a.`password` = #{password}

mapper.update:
update userinfo_ext t
	set t.userid = #{userid}
	
		,t.zhuye = #{zhuye}
	
	
		,t.zantong = #{zantong}
	
	
		,t.yanzhi = #{yanzhi}
	
	
		,t.ptname = #{ptname}
	
	
		,t.ptid = #{ptid}
	
	
		,t.hangye = #{hangye}
	
	
		,t.fensi = #{fensi}
	
	where t.userid = #{userid}
	  and t.pttype = #{pttype}

UPDATE userinfo t
	set t.username = t.username
	
		,t.wechat = #{wechat}
	
	
		,t.phone = #{phone}
	
	
	,t.alipay = #{alipay}
	
	
	,t.factname = #{factname}
	
	where t.userid = #{userid}
